mapper.select:
select
    p.name as placeName, region_1depth,region_2depth,region_3depth , pk.kind as place_kind, rating, (select count(*) from review where place_no=p.no) as review_count, p.price_high as price
from
    place p left outer join place_kind pk on p.place_kind_code = pk.no



            region_1depth = #{region1depth}


            and region_2depth = #{region2depth}


            and region_3depth = #{region3depth}
